css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("instagram.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h2, h3, h4, h5, h6, font, strong, p, form, footer, header

{
color: white !important;
background: black !important;
}

select, button, section, article, #home-bg, ._76y, .sidebar-nav, .main, .timelineCenter, .timelineComments, .timelineLikes, .vcCell, .iContentArea, .FeedbackBar, .ImageBlock, .compContainer, .profile-user, .bg, .button, .Dropdown ul li a:hover

{
background: black !important;
}

span

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option, h1

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
