css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {   

#dashboard_index, #dashboard_post_queue, #dashboard_followers, #dashboard_members, #dashboard_likes, #dashboard_following, #dashboard_directory {
background: #000000 url(http://i133.photobucket.com/albums/q79/ToxicCosmos/stbg-1.jpg) no-repeat fixed 0% 100% !important;
}

#dashboard_index div.l-content, #dashboard_post_queue div.l-content, #dashboard_followers div.l-content, #dashboard_members div.l-content, #dashboard_likes div.l-content, #dashboard_following div.l-content, #dashboard_directory #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color:rgba(0,0,0,0.5)!important;
}    

ol#posts li {
background-color:rgba(53,60,62,0.5)!important;  
color: #A8BCCB !important;
}

.audio_label a.title {
color: #A0A0A0 !important;
}

.video_option textarea {
color: #A0A0A0 !important;
}

.tag_editor {
color: #A0A0A0 !important;
}

#dashboard_index a {
color: #A0A0A0 !important;
}

#dashboard_index a:visited {
color: #A0A0A0 !important;
}

#dashboard_index a:hover {
color: #A0A0A0 !important;
}

.post_form_wrapper {
background-color:rgba(0,0,0,0.5)!important;
}

#dashboard_index .scrollverlay_active {
background-image: none !important;
background-color:rgba(0,0,0,0.5)!important;
}

}
