css:
@-moz-document domain('www.evernote.com') {
    body > div > div {
        top: -37px !important;
    }

    .gear-dropdown {
        top: 32px;
        position: relative;
        background-color: #666 !important; 
        border-bottom-left-radius: 10px;
    }

    body > div > div:last-child > div > div:nth-child(2) > div > div:last-child {
        right: 120px !important;
    }
}
