css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(chrome://browser/content/aboutDialog.xul) {

#aboutDialogContainer{
    background:black !important;
    background-position:center center !important;
    background-size:80% !important;
}

#clientBox{
    background:url('http://i1339.photobucket.com/albums/o712/kalxd/Stylish/5199652033_96c158f23a_z_zps6024247f.jpg') !important;
    border-radius:5px;
    border-bottom:1px white solid;
}

#leftBox{
    background:url('http://i1339.photobucket.com/albums/o712/kalxd/Stylish/firefox_zps21899cd7.png') !important;
    background-position:0 0 !important;
    background-size:100% !important;
}

#version{
    font-weight:bold;
    color:white !important;
}

#detailsBox{
    color:white !important;
    text-shadow:1px 1px 7px rgba(0,255,255,.6) !important;
}

#updateButton{
    -moz-appearance:none !important;
    background:transparent !important;
    color:white !important;
    text-shadow:0 0 10px red !important;
    border-radius:10px;
    border:1px rgba(0,255,255,.7) solid !important;
    border-left:none !important;
    border-right:none !important;
    padding:3px !important;
    -moz-transition:all .5s ease-in-out;
}
#updateButton:hover{
    background:rgba(0,45,200,.8) !important;
    box-shadow:0 0 5px 0 #0ff;
}

#noUpdatesFound{
    background:rgba(0,0,0,.5) !important;
    border-radius:5px !important;
    border-bottom:1px rgba(0,200,200,.7) solid !important;
    border-top:1px rgba(0,222,30,.7) solid !important;
    font-weight:bold !important;
}
}
