css:
@-moz-document domain(tumblr.com) {
body {
  background:url("http://media.tumblr.com/6dda9c40a42dae32f2085636831e1a95/tumblr_inline_mhrwcyPrg01qz4rgp.jpg") no-repeat fixed !important;
  background-color: #000000 !important;
  background-position: 50% 30% !important;
  background-size: 1600px 100px  !important;
}


.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background-image:url("http://i50.tinypic.com/15qxc3p.jpg")!important;background-repeat:no-repeat!important;width:18px!important;height:15px!important;margin-top:-15px!important}.selection_nipple.white{background-position:0 0!important;width:18px!important;height:15px!important}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}

#inbox_button.tab.iconic.selected .selection_nipple:not([class="selection_nipple white"]){margin-left:-9px!important;left: 1px !important;}

#home_button.tab.iconic.selected .selection_nipple:not([class="selection_nipple white"]){margin-left:-9px!important;left: 0px !important;}


li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}



.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}


div.l-header .tab .tab_notice, #popover_blogs .tab_notice, #dashboard_controls_open_blog .tab_notice {font-family: "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif !important;}

.pill.notice{
color: rgba(255,255,255,0.50)!important;}

.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
}
