css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("github.com") {
.header, .header a, .commit-group-heading, .discusion-topic-infobar, .comment-header, .thread-subscription-status, .gobutton, .top-bar, .bottom-bar, .pull-info, .inner, .capped-box h3, #readme .name, .col h3, .tabnav-tabs .selected, .list-browser-filterbar, .list-browser-footer, #commit-activity-master-head {
    background: #111 !important;
    text-shadow: none !important;
    color: #eee !important;
}
.hentry, pre, code, #footer-push, body, .minibutton, .drag-and-drop {
    background: #2C2C2C !important;
}
dt, dd, p, a, .footer-text, div, span {
    text-shadow: none !important;
}
.commit, .commit-meta, .diff-table .diff-line, .author, .comment-body, .gravatar, .markdown-format, textarea, .tip, .discussion-bubble-inner, .line_numbers, .push, .meta, .popular, .filter-bar, .bootcamp-body, .compact, #contributors-master, .css-truncate, .capped-box, .person, #commit-activity-detail, .filterbox, .none p, .content, .icon, .age, .message, .info-wrapper, .read-status, .number, .entry-content, .not-editable {
    background: #333 !important;
}
.warn {
    background: #543 !important;
}
.gc.diff-line.line {
    color: #aaa !important;
    background: #203201 !important;
}
.gd.diff-line.line {
    background: #550000 !important;
}
.gd.diff-line .x {
    background: #880000 !important;
}
.gi.diff-line.line {
    background: #005500 !important;
}
.gi.diff-line .x {
    background: #008800 !important;
}
.warn h2 {
    color: #210 !important;
}
h2, h4, #network b, h2 a, ul, pre, .email-fragment, .user-mention, .commit-author a, .info-wrapper a, .comment-header-author, .minibutton, textarea, .data .line, p, .x, code, .clearfix h3, .compact a, .warn p, .message, .authorship a, .stats strong, .inner strong, .entry-content, .clear-issue-filters, .js-navigation-open, .details dd {
    color: #eee !important;
}
.js-relative-date, .js-selectable-text, p strong, .sha, .count, .title, .branch-link, .issue-link {
    color: #ddd !important;
}
}
