css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix("http://developer.echonest.com"),
url-prefix("http://blog.echonest.com") {

/* Dark Grey BG */
body
{color: #222 !important;}
body,
div.body
{background-color: #222 !important;}
div.document,
div.pagetop
{background: #222 !important;}

/* Black BG Highlight */
pre,
div.sphinxsidebarwrapper,
div.body h1,
div.body h2,
blockquote blockquote
{background-color: #111 !important;}


/* Light Grey Font */

p,
li,
strong,
td,
div.body,
div.body h1,
div.body h2
{color: #666 !important; /* Light grey text color since these are mostly static text */}  


}
