css:
/* AGENT_SHEET */
html > scrollbar scrollbarbutton {
    display: none !important}
html > scrollbar {
    -moz-appearance: none !important;
    background: none !important;
    box-shadow: none !important;
    position: relative !important;
    z-index: 99999 !important}
html > scrollbar thumb {
    -moz-appearance: none !important;
    -moz-transition-duration: 3s !important;
    -moz-transition-property: background, border !important;
    background: rgba(150,150,150,.5) !important;
    box-shadow: inset 0 0 1px rgba(255,255,255,.5) !important;
    border-radius: 10px !important;
    border: 2px solid transparent !important;
    background-clip: padding-box !important;
    position: relative !important}

html > scrollbar:hover thumb {
    background: rgba(80,80,80,.7) !important;
    background-clip: padding-box !important}
html > scrollbar thumb:active {
    background: rgba(100,100,100,.5) !important;
    border: 1px solid transparent !important;
    background-clip: padding-box !important}

html > scrollbar[orient="vertical"] {
    margin-left: -8px !important}
html > scrollbar[orient="vertical"] thumb {
    min-width: 8px !important;
    max-width: 8px !important;
    min-height: 30px !important}

html > scrollbar[orient="horizontal"] {
    margin-top: -8px !important}
html > scrollbar[orient="horizontal"] thumb {
    min-height: 8px !important;
    max-height: 8px !important;
    min-width: 30px !important}
