css:
/*
  Yahoo Groups No Ads + No Serifs + Minimal
  June 23 2008 - v0.1
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("groups.yahoo.com") 
{

/* top header --uncomment this for an even more minimal look*/
/*div[id="ygma"], */

/* web search form */
form[id="ygmafrm"],

/*top banner ad*/
tr[bgcolor="#ffffff"][valign="middle"],

/* Yahoo Groups Tips */
[id="ygrp-gtips"], 

/* Best of Yahoo Groups */
[id="ygrp-mgl-promo"], 

/* yahoo copyright footer */
div[id="doc-foot"],

/*google ads*/
div[id="ygrp-sponsored-links"]

{
display: none !important;
}


/* FONTS */
* {
font-family: Arial, Helvetica, Garuda, sans-serif !important;
}

}
