css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
/*TUTTO*/
body {
background-image: url(http://img.chan4chan.com/img/2010-01-18/1263818375209.jpg) !important;
background-attachment: fixed !important;
}
a {
color: #ffcc00 !important;
text-shadow: none !important;
}
a:hover {
text-shadow: 0px 0px 3px #ff0000 !important;
}

#logo {
background-image: url(http://img.chan4chan.com/img/2010-01-18/1263818375209.jpg) !important;
background-position: center !important;
}
/*HOME PAGE*/
.box-outer {
color: #fff;
background-color: #000 !important;
}

#announce .boxcontent {
text-shadow: 0px 0px 15px #fff !important;
}

#ft li{
background-image: none !important;
background-color: #000 !important;
}

/*POST*/
.post {
text-shadow: 0px 0px 10px #000 !important;
}
.reply{
border-radius: 10px;
border: 0px solid rgba(0, 0, 0, 0) !important;
background-color: rgba(0, 0, 0, 0) !important;
box-shadow: inset 3px 3px 5px #000 !important;
padding-top: 7px !important;
padding-left: 7px !important;
color: #000 !important;
text-shadow: 0px 0px 10px #888 !important;
}
.name {
color: #0055ff !important;
}

}
