css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("presto.fm") {

/* Hide {display: none !important;} */
.pointing-arrow-bottom 
    {display: none !important;}

/* Dark BG-color {background-color: #222 !important;} */
body,
#artist-view
    {background-color: #222 !important;}

/* Black BG-color {background-color: #111 !important;} */
#perpetual-now-playing
    {background-color: #111 !important;}

/* Font {color: #666 !important;} */
body,
h1, h2, h3, h4, h5, h6
    {color: #666 !important;}

}
