css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.sendspace.com/file/) {

/* delete ad */

div#android_dl,
div#sswxdownload,
div.info>center,
div.info>div:nth-of-type(2),
div.info>div.text>div#spndllink>div:nth-of-type(2),
div.info>div:nth-of-type(4) {
    display: none !important;
}

/* adjust line-height */

div.info>div.text {
    line-height: 0px !important;
}

/* delete header & footer */

div#header,
div#headline,
div#footer {
    display: none !important;
}

/* adjust content margin-top */

div#content {
    margin: 100px auto 0px auto !important;
    border-top: 1px solid rgb(214, 214, 214) !important;
}

}
