@-moz-document domain("tolkuchka.dsip.net") {
th[width*="7%"] {
  width: 1% !important;
}

th[width*="14%"] {
  width: 1% !important;
}

th[width="22%"] {
  width: 30% !important;
}

th[width="50%"] {
  width: 60% !important;
}

div[align="center"] > a, #logostrip, div.globalmesswarnwrap, .lastaction br, .copyright, a[rel*="noreferrer"], iframe, object, a[href*="top100"], a[href*="banner"] {
  display: none !important;
}

td.row1 img[src*="folder_post_icons"] {
  height: 12px !important;
}

.minipagelink, .minipagelinklast {
  padding: 0px !important;
  fl---oat: right !important;
}

div.borderwrap table tbody tr td div a[href*="multi_page_jump"], div.borderwrap table tbody tr td div a[onclick*="PopUp"] {
  fl---oat: right !important;
}

div.borderwrap table tbody tr td div a, div.borderwrap table tbody tr td div .desc {
  fl---oat: left !important;
  display: inline;
}

.lastaction a, .desc {
  padding-left: 5px !important;
}

font[color="red"] {
    fl---oat: left !important;
	display: inline;
    margin-right: 5px !important;
}

#navstrip, p, td {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}
}
