css:
@-moz-document url-prefix('http://www.sjp.pl') {
body {
    background-color: black;
    margin: 1.5em auto;
    width: 52em;
    word-wrap: break-word;
}
body, input {
    color: white;
    font-family: Verdana,sans-serif;
    font-size: small;
}
h1 {
    font-family: sans-serif;
    font-size: xx-large;
}
a:link, ul.m a, .lc {
    color: #F23380;
}
a.nu {
    text-decoration: none;
}
a.nu:hover {
    text-decoration: underline;
}
ul.m {
    list-style-type: none;
    margin: 0.5em 0;
    padding: 0 0 0.5em;
}
ul.m li {
    border-left: 1px dotted #AAAAAA;
    display: inline;
    margin: 0;
    padding: 0 0.5em;
}
ul.m li.f {
    border-left: 0 none;
    padding-left: 0;
}
hr {
    background-color: #AAAABB;
    border: 0 none;
    height: 1px;
}
p, li {
    color: #33F2B9;
    line-height: 1.4em;
}
.z p {
    margin-bottom: 2em;
    max-width: 34em;
}
.x {
    margin-right: 0.5em;
}
.y, .y a {
    color: #777777;
}
.y {
    margin-left: 0.5em;
}
.y, .s {
    color: #98E01B;
    font-size: x-small;
}
tt {
    font-family: monospace,monospace;
    font-size: small;
}
.wf {
    border: 1px solid #CCDDFF;
    border-collapse: collapse;
}
.wf td, .wf th {
    border: 1px solid #CCDDFF;
    color: #223355;
    padding: 0.4em 0.7em;
}
.wf th {
    font-weight: normal;
    text-align: left;
}
}
