css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pastebin.com") {
html, body, #code_buttons {
    background: #2C2C2C !important;
}
#monster_frame, .paste_code, textarea {
    background: #333 !important;
    color: #eee !important;
}
h1, #content_right div, #code_frame ol div, textarea {
    color: #eee !important;
}
#code_frame, .textarea_border {
    background: #111 !important;
}
#code_frame .de1 {
    background: #2F2F2F !important;
}
#code_frame .de2 {
    background: #3A3A3A !important;
}
#footer_top, #footer_right {
    display: none !important;
}
#super_frame > div > span {
    display: none !important;
}
a {
    color: steelblue !important;
}
.kw2 {
    color: #AAC0FF !important;
}
.kw3 {
    color: #4870EC !important;
}
.sy0 {
    color: #00AA00 !important;
}
.co1 {
    color: #006600 !important;
}
.me1 {
    color: #AA33BB !important;
}
.nu0 {
    color: #D92233 !important;
}
a, div, span, li, p {
    text-shadow: none !important;
}
}
