css:
@-moz-document domain("gigazine.net") {
#header, #latest, #sub, #EndFooter, footer, #UFad
{ display: none !important; }

body {
  background-color: #f5f5f5;
}

#main {
  width: 800px;
  margin: 20px auto 100px;
  border: 1px solid #e0e0e0;
  padding: 0 40px;
  background-color: #fff;
}

#main > #article {
  border: 0;
}

/**
 * Top page
 */
.content section {
  width: 25%;
}
.content section .card {
  width: 94%;
}
.content section .card h2 {
  font-size: 14px;
}
.content section .card h2 a {
  background: #fff7c0;
}
.date time, .catab {
  font-size: 11px;
}
.date time a, .ctlink {
  color: #bbb !important;
}
}
