css:
@-moz-document url("http://secma-f16passion.forumactif.org/") {
html body #main-content > div:first-child {
	display: none !important;
}

div.panel {
	display: none;
}

div[align=right] {
	display: none;
}

ul.topiclist dd.dterm {
	margin-left: 100px;
	width: 600px;
}

dd.posts, dd.topics, dd.views {
	width: 50px;
}

dd.lastpost {
	width: 270px;
}

ul.topiclist.forums > li.row:last-child {
	display: none;
}

h2.hierarchy {
	font-size: 1.2em !important;
}
}
