@-moz-document domain("last.fm") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("last.fm") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

selector {
	text-transform: capitalize!important;
}

.features-teaser-has-video,
.recs-feed .context,
.wiki-cta,
:focus {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.music-tags {
    grid-template-columns: repeat(6,86rem);
    grid-template-rows: repeat(5,57rem);
}

.artist-similar-artists-sidebar-item-name {
    overflow: visible!important;
}

/*___ Fonts ________________________________________________________________________*/

.thumbnails-b .syndicatedItem .video-title,
body {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

h1 {
	font-size: 24px!important;
}

.wiki-content,
body {
	font-size: 19px!important;
}

.kerve.widget .widget_intro {
	font-size: 17px!important;
}

selector {
	font-size: 15px!important;
	line-height: 27px!important;
}

.kerve.widget .widget_intro {
	line-height: 23px!important;
}

/*___ Remove Items _________________________________________________________________*/

.features-teaser-wrap--promoted.features-teaser-wrap,
.full-bleed-ad-container,
.lastfm-ad,
.lazy-features-footer.async-content-loaded,
.mpu-subscription-upsell--mpu,
.related-ads,
.sidebar-ad-container,
.subscribe-cta,
.trc_related_container
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.grid-items--big-first .grid-items-item--big .grid-items-item-details {
	bottom: 0!important;
	top: 39rem!important;
}


.selectable-range .grid-items-item-details {
	bottom: 0!important;
	top: 20rem!important;
}

.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-cover-image .grid-items-item-details {
	bottom: 0!important;
	top: 27rem!important;
}

.recs-feed .recs-feed-cover-image-content {
	bottom: 0!important;
	top: 40rem!important;
}

.recs-feed .context {
	bottom: 0!important;
	top: 7rem!important;
}

.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-item {
	padding-top: 57%!important;
}

.features-header .features-teaser-body {
    bottom: 19px;
}

/*___ Margins ______________________________________________________________________*/

.features-post-main,
.features-post-top,
.features-teaser-wrap,
.charts-col,
.col-main,
.col-sidebar,
.grid-items-item,
.music-section,
.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-item,
.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-section
{
	margin: 12px!important;
}

.shout-list-item {
	margin: 3rem!important;
}

/*___ Paddings _____________________________________________________________________*/

.features-post-main,
.features-post-top,
.features-teaser-wrap,
.charts-col,
.col-main,
.col-sidebar,
.dashboard .kerve.widget,
.music-section,
.shout-list-item,
.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-section
{
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

.artist-similar-artists-sidebar-item-name {
	max-height: none!important;
}

.dashboard .kerve.widget {
	height: auto!important;
}

.recs-feed .recs-feed-item {
    height: 50rem;
}

.grid-items-item {
	width: 192px;
	height: 27rem;
}

.masonry-right {
    height: 26rem;
}

/*___ Widths _______________________________________________________________________*/

.artist-similar-artists-sidebar-item-name {
	max-width: none!important;
}

selector {
	width: auto!important;
}

.features-post-body,
.features-post-header-body {
    width: 100%;
}

.header-new-content,
.container,
.dashboard .kerve.widget.widget_spike,
.kerve.widget.widget_now2 {
	width: 98%!important;
}

.features-post-main {
    width: 87%;
}


.col-main {
	width: 70%;
}

.metadata-and-wiki-row--artist .wiki-column {
    width: 62%;
}

.charts-col {
    width: 48%;
}

.dashboard .kerve.widget {
	width: 47%!important;
}

.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-section {
	width: 47%;
}

.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-item {
	width: 45%!important;
}

.metadata-and-wiki-row--artist .metadata-column,
.artist-header-featured-items-item-wrap,
.artist-header-featured-items-item-wrap:last-child {
    width: 35%!important;
}

.recs-feed .recs-feed-item {
    width: 33%!important;
}

.header-new--artist.header-new--overview .header-new-background-image,
.header-new--artist.header-new--overview .header-new-background-overlay {
    width: inherit!important;
}

}

@-moz-document domain("last.fm") {
/*__________________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.globalchart .globalchart-rank,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.catalogue-metadata-description,
.wiki-block-inner-2,
.recs-feed .context,
body,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.chartlist .chartlist-index,
.factbox-summary,
.music-featured-tag-featured-artist,
.thumbnails-b .syndicatedItem .video-title,
.thumbnails-b .trc_rbox_header,
p strong,
h1,
h2 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.artist-header-featured-items-item-header,
.metadata-and-wiki-row .catalogue-metadata .catalogue-metadata-heading,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.features-post-byline,
.features-teaser-byline-item,
h5,
h6,
label {
	color: #F0A060!important;
	text-shadow: 2px 2px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.features-post-byline:nth-child(2),
.features-teaser-byline-item:nth-child(2),
.artist-header-featured-items-item-aux-text.artist-header-featured-items-item-date,
.artist-featured-items-item-aux-text.artist-featured-items-item-date,
.factbox-item:first-child p,
.kerve .trendwrap .axis_x .axislabel,
.metadata-item:first-child p,
time {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

.dropdown-buylinks .dropdown-featured-buylink {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

selector {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
	/* Chrome, Firefox, Opera, Safari 10.1+ */
	color: #f48f8f;
	opacity: 1;
	/* Firefox */
}


}

@-moz-document domain("last.fm") {
/*__________________________________________________________________________________*/

.adaptive-skin-wrapper,
.centered-layout .page-content,
.page-content,
.two-column-layout .container,
body,
html {
	background: #000 url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.music-featured-item-body {
	background: #0000001c;
}

/*___ Background Gray ______________________________________________________________*/

.footer,
.header--overview
{
	background-color: #060505!important;
}

.content-top-has-nav .content-top,
.two-column-layout .content-top {
	background-color: #0a0909!important;
}

/*___ Background 0000 ______________________________________________________________*/

.chartlist .countbar-bar-slug,
.features-header .features-teaser-body,
.features-post-header-body,
.features-teaser-has-video,
.features-teaser-wrap--promoted .features-teaser-body,
.globalchart .globalchart-item *,
.header-new--artist.header-new--overview .header-new-background-overlay,
.recs-feed .context,
[style="background: #2D4A6A"]
{
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.artist-top-albums-item .lazy-buylinks-toggle,
.artist-top-albums-item-playlink:focus:before,
.artist-top-albums-item-playlink:hover:before,
.chartlist tr.chartlist-row--even,
.section-with-separator:after
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Background _________________________________________________________*/

.grid-items-cover-image-image:after,
.two-column-layout .container {
	background-image: none!important;
}

/*__ Blue __________________________________________________________________________*/

selector {
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red __________________________________________________________________________*/

.correction-vote-no-on,
.btn-primary {
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.correction-vote-no-on:hover,
.btn-primary:hover {
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

.correction-vote-no-on:active,
.btn-primary:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Gray ________________________________________________________________________*/

.correction-vote-yes-off,
.correction-vote-no-off {
	background-image: linear-gradient(to bottom right, #999, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.correction-vote-yes-off:hover,
.correction-vote-no-off:hover {
	background-image: linear-gradient(to bottom right, #aaa, #000)!important;
	color: #ff0!important;
}

.correction-vote-yes-off:active,
.correction-vote-no-off:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

.correction-vote-yes-on {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.correction-vote-yes-on:hover {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

.correction-vote-yes-on:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

.col-main,
.col-sidebar,
.dashboard .kerve.widget,
.features-post-main,
.features-post-top,
.features-teasers,
.music-section,
.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-item
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.col-main:hover,
.col-sidebar,
.dashboard .kerve.widget:hover,
.features-post-main:hover,
.features-post-top:hover,
.features-teasers:hover,
.music-section:hover,
.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-item:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.charts-col,
.features-teaser-wrap,
.kerve.widget,
.modal-content,
.shout-list-item,
.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-section
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.charts-col:hover,
.features-teaser-wrap:hover,
.kerve.widget:hover,
.modal-content:hover,
.shout-list-item:hover,
.similar-tracks-and-artists-row.similar-tracks-and-artists-row--columns .grid-items-section:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.kerve a.btn_trendnav.current {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

.chartlist tr:hover,
.feedback-message,
.kerve .trendglobal,
.kerve .trenduser
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
}

/*__ Blue Gradient _________________________________________________________________*/


.tag-cta,
.wiki-cta {
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient ________________________________________________________________*/

.alert-success {
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Yellow Gradient _______________________________________________________________*/

.kerve a.btn_trendnav {
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Orange Gradient _______________________________________________________________*/

.chartlist-count-bar-slug {
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}
}

@-moz-document domain("last.fm") {
/*__________________________________________________________________________________*/

.dashboard .kerve.widget,
.kerve .spikewrap .bubble_img,
.shout-list-item,
.trc_elastic_thumbnails-b .trc_rbox_outer .videoCube
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.dashboard .kerve.widget:hover,
.kerve .spikewrap .bubble_img:hover,
.shout-list-item:hover,
.trc_elastic_thumbnails-b .trc_rbox_outer .videoCube:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

.header-new--artist.header-new--overview .header-new-background-image, .header-new--artist.header-new--overview .header-new-background-overlay,
img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/


li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.music-featured-item-body p,
.shout-list-item:hover p,
li p.header-metadata-tnew-display abbr,
li:hover,
p.artist-header-featured-items-item-aux-text,
p:hover,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

.artist-header-featured-items-item-header,
.player-bar .player-bar-btn.inactive,
.player-bar .player-bar-btn.inactive:focus,
.player-bar .player-bar-btn.inactive:hover,
.player-bar.inactive .player-bar-btn
{
    opacity: 1;
}
}

@-moz-document domain("last.fm") {
/* Hearts _________________________________________________________________*/

.player-bar .player-bar-btn--love:before {
    width: 39px;
    top: 65%;
    left: 68%;
}

.player-bar .player-bar-btn--love:before {
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat;
	background-size: 31px;
}

[data-toggle-button-current-state="unloved"] a.chartlist-love-button:before, [data-toggle-button-current-state="unloved"] button.chartlist-love-button:before {
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat;
	background-size: 22px;
}

[data-toggle-button-current-state="loved"] a.chartlist-love-button:before, [data-toggle-button-current-state="loved"] button.chartlist-love-button:before {
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat;
	background-size: 22px;
}


.recs-feed .context-love-icon:before {
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat;
	background-size: 16px;
	width: 17px;
    height: 15px;
}

/* Play Buttons ____________________________________________________________*/

.stationlink-list .stationlink:before {
    background: url(https://i.ibb.co/pvSw87h/Play-54x54.png) no-repeat;
    background-size: 31px;
}

.artist-top-albums-item-playlink:before,
.recs-feed .recs-feed-playlink:before {
    background: url(https://i.ibb.co/pvSw87h/Play-54x54.png) no-repeat;
    background-size: 51px;
}

/* Top Play Button _________________________________*/

.player-bar .player-bar-btn--play:before {
    top: 59%;
}

.artist-header-featured-items-item-playlink:before,
.artist-header-featured-items-item-video .image-overlay-playlink-link:before {
    background: url(https://i.ibb.co/pvSw87h/Play-54x54.png) no-repeat;
    background-size: 49px;
