css:
@-moz-document url("http://www.theregister.co.uk/") {
.index .story-ref {
  width: 100%;
}

.index .story-ref {
  float: left;
  padding-left: 0;
}

h3, .story-ref h3, .index .first-row h3 {
  font-size: 22px;
}

.story-ref h3 a:visited {
  color: #36A;
}
.story-ref h3 a:link {
  color: #36A;
}

#masthead, .teaser-boxed, #top-nav, #sub-nav, .stamp, #am, .toprot, #right-col {
  display: none;
}
}
