css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {

/* resize the question asker text to match other username text */
.post_question_asker, .post_question {
font-size: 14px !important;
}

/* resize boxes on questions to not be ugly */
.asking_avatar {
margin-bottom: -15px;
}

/* other links aren't underlined, so this one shouldn't be */
.post_ask_me_link {
text-decoration: none !important;
}

.post_ask_me_link:hover {
text-decoration: underline !important;
}

/* unify link style in posts */
.post_wrapper a {
text-decoration: none !important;
color: rgb(30, 95, 150) !important;
font-weight: bold;
}

.post_wrapper a:hover {
color: rgb(68, 68, 68) !important;
/*text-decoration: underline !important;*/
}

/* Sometimes post titles are really big. Fix this. */
.post_title {
font-size: 16px !important;
font-weight: bold !important;
line-height: 1.3 !important;
}

/* dear god why is this text bold for no reason at all */
/* also fix the padding on the quote box */
.post_question {
font-weight: normal !important;
font-style: italic;
padding-top: 8px !important;
padding-bottom: 12px !important;
}

/* for some dumb reason, inline images on the dash have max-width set */
img.inline_image {
max-width: 100% !important
}

/* remove sponsored posts */
.sponsored_post {
display: none !important;
}

/* nicer coloring */
body#dashboard_index {
background: rgb(44, 71, 98) !important;
}

div.selection_nipple {
display: none !important;
}

/* make the new post bar better */
#post_buttons > a:hover {
text-decoration: underline !important;
}

#new_post_label_text {
background-position: 2px -5px;
}

#new_post_label_photo {
background-position: -73px -5px;
}

#new_post_label_quote {
background-position: -148px -5px;
}

#new_post_label_link {
background-position: -223px -5px;
}

#new_post_label_chat {
background-position: -298px -5px;
}

#new_post_label_audio {
background-position: -373px -5px;
}

#new_post_label_video {
background-position: -448px -5px;
}

.new_post_label_icon {
background-position: inherit !important;
}

/* What is this horrible fuzziness? Not the 1940s anymore */
.retro_fuzz {
display: none !important;
}

/* The search box text is absurdly big. Let's fix it. */
.help_search input {
font-size: 16px !important;
background-position: 0px -70px !important;
}

/* Make play button prettier */
.big_play_button {
background: none !important;
box-shadow: none !important;
border-width: 2px !important;
}

.safety_glass {
background: none !important;
}

/* 6px rounded corners are ugly, 2px is much better */
.post_avatar, .post, #search_query, .search_popover {
border-radius: 2px !important;
}

#right_column .controls_section li:first-child{
    border-top-left-radius: 2px !important;
    border-top-right-radius: 2px !important;
}


#right_column .controls_section li:last-child, .radar_controls {
    border-bottom-right-radius: 2px !important;
    border-bottom-left-radius: 2px !important;
}

/* improve width of search popover */
.search_popover {
width: 275px !important;
}

.search_popover .result_link {
max-width: none !important;
}

.search_popover .result.has_thumb .hide_overflow {
max-width: 210px !important;
}



/* out of scope */
}
