css:
/* name: BEAT POPS CLUB hatena hideaway_juju ja jp 2013/03 */
@-moz-document url-prefix("http://d.hatena.ne.jp/hideaway_juju/"){
/*== BEAT POPS CLUB on hatena.ne.jp (http://d.hatena.ne.jp/hideaway_juju/) ==*/
/*- generic font style to default -*/
* {font-family: inherit !important; font-size: inherit !important;}

/*- page -*/
body>* {width: inherit !important; mi-width: 800px;}
/*- page hatena header -*/
#simple-header {margin: 0 !important; padding: 0.5em 0px !important;}
/*- page title -*/
h1 {padding: 0.5em !important; font-weight: bold !important;}

/*- blog body 2columns requid relayout -*/
.hatena-body>.main {
 width: 100% !important; margin-right: -200px !important; border: none !important;}
.hatena-body>.main>div
 {width: auto !important; margin-right: 200px; border-right: 2px dashed #726250;}
/*- blog body text to higher contrast -*/
.hatena-body * {color: white !important;}

}
