@-moz-document domain("khanapakana.com") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("khanapakana.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.yafnet tr.header2 td a,
.yafnet tr.header2 th a,
.yafnet .menuList li a,
.yafnet .menuAdminList li a {
	text-transform: capitalize!important;
}

.yafnet .yafPageLink,
.yafnet .menuContainer,
.subcontent-video-box,
.subcontent-box,
.subcellCat,
.subcellCat1,
.CELL h2,
.CELL .subcell {
	border: none!important;
}


.searchform {
    float: none!important;
}

.searchbox {
    float: right!important;
}

.content .CELL h2 {
    top: 0px!important;
}

/*___ Fonts ________________________________________________________________________*/

#related-thumbs .rtitle a,
#tabsI ul,
.buzz-links ul li a,
.buzz-links,
.CELL p,
.CELL ul li a,
.content p,
.main a,
.recipes h3,
.recipes,
.yafnet .menuAdminList li a,
.yafnet .menuList li a,
.yafnet .yafPageLink,
.yafnet td,
.yafnet tr.header2 td a,
.yafnet tr.header2 th a,
body {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

#related-thumbs .rtitle a,
.yafnet .yafPageLink {
	font-size: 25px!important;
	line-height: 30px!important;
}

#related-thumbs span,
.main a,
.order_list ol p,
.recipes .recipe,
.yafnet .menuAdminList li a,
.yafnet .menuList li a,
.yafnet td,
.yafnet tr.header2 td a,
.yafnet tr.header2 th a {
    font-size: 19px!important;
}

#tabsI ul,
body {
	font-size: 17px!important;
}

selector {
	font-size: 14px!important;
	line-height: 19px!important;
}

.content ul li {
    font-size: 100%!important;
}

.buzz-links ul li {
    line-height: 1.5em!important;
}

.content p {
    line-height: 28px!important;
}

/*___ Remove Items _________________________________________________________________*/

.header,
.main-banner-left,
.subcontent-box,
.vm-hook-outer.vm-hook-instory {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.rectable {
    margin-top: -10px!important;
}

/*___ Margins ______________________________________________________________________*/

#chef_cont ul li,
#related-thumbs .cont,
.buzz-links,
.content-1col-nobox,
.recipes,
.subcontent-video-box,
.yafnet .post,
.yafnet .post_alt {
	margin: 12px!important;
}

.content-12col-nobox:first-child {
	margin: 3rem 0 12px!important;
}

.content-12col-nobox {
	margin: 3rem 0 12px!important;
}

.subcellCat-v {
    margin: 1rem -4rem!important;
}

#tabsI ul {
	margin-bottom: 3rem!important;
}

.searchbox {
    margin: 3px!important;
}

.subcellCat {
    margin: 0 0 0 -2rem!important;
}

/*___ Paddings _____________________________________________________________________*/

#cloud,
#related-thumbs .cont,
.content-1col-nobox,
.recipes,
.subcellCat,
.yafnet .post,
.yafnet .post_alt {
	padding: 12px!important;
}

#chef_cont ul li {
	padding: .5rem .5rem 2.5rem .5rem!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

selector {
	height: auto!important;
}

.text-wrapp1 {
    height: 32px!important;
}

.buzz-links {
    height: 1.8rem!important;
}

/*___ Widths _______________________________________________________________________*/

body {
	max-width: none!important;
}

#cloud,
#footerT .inner_f2 .fltL:first-child,
.CELL .subcell,
.container,
.footer,
.headerbottom,
.popularcusine,
.recipes .recipe,
.searchdiv,
.subcellCat1,
.subcontent-box,
.wrapper1,
.wrapper2 {
	width: 100%!important;
}



#related-thumbs .cont,
.subcontent,
.searchform {
    width: 99%!important;
}

#footerT .inner_f2,
#footerT .inner_f3,
#related-thumbs,
.buzz-links,
.content,
.content-1col-nobox,
.content-main,
.main,
.subcellCat,
.subcontent-video-box {
    width: 98%!important;
}

#related-thumbs .right-c,
.rectable {
    width: 92%!important;
}

.subcellCat-v {
    width: 82%!important;
}

.NewsletterSubscription input[type="text"] {
    width: 65%!important;
}

.content-12col-nobox {
    width: 48%!important;
}

.CELL {
    width: 32%!important;
}

#footerT .inner_f3 .fltL {
    width: 20%!important;
}

#footerT .inner_f2 .fltL .Block {
    width: 33%!important;
}


#footerT .inner_f2 [class*="pl"] {
    width: 29%!important;
}



















}

