css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rateyourmusic.com") {

/* Hide {display: none !important;} */
#ebaylistings,
a.buybutton, a.buybutton:hover, a.buybutton:visited
    {display: none !important;}

/* Dark BG {background: #222 !important; color: #666 !important;} */


#content,
div.profileb,
div.profileh,
#navbottom ul,
table,
div,
#tracklisting.mbgen tr,
tr.selectedissue
    {background: #222 !important; color: #777 !important;}

.tblstandard, .mbgen,
.discography,
.mbgen th, #userlists th, .discography th,
.shaded,
#membersOnline th
    {background-color: #222 !important; color: #777 !important;}

/* Black BG {background: #111 !important; color: #666 !important;} */
html,
body,
#top,
#navtop,
#breadcrumb,
#content
    {background: #111 !important; color: #666 !important;}


/* Border Black */
.tblstandard td, .tblstandard, th, .mbgen td, .mbgen th,.tblstandard, .mbgen, .discography,
#membersOnline td
    {border:1px solid rgb(0,0,0) !important;}

/* Orange */
a:link, a:visited
    {color: #FDC423 !important;}

}
