css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("point.md") {

    body {
        padding-top: 30px;
    }
    
    .right-sidebar,
    .top-nav-wrap,
    .link-to-point,
    .top-banner-block,
    #topbar-panel,
    #topbar,
    .post-comments,
    .more-news-wrap,
    .alert-typo {
        display: none !important;
    }
    
    .news-page .middle-block {
        margin: 0;
        width: 767px !important;
    }
    .left-sidebar {
        width: 193px !important;
    }

    .wrapper {
        width: 960px;
        min-height: 0;
    }
    
    .header {
        text-align: left !important;;
    }
    
    .search-input {
        font-size: 16px;
        padding: 0 0 0 8px;
    }
    
    .post-footer {
        height: auto;
    }

    section.topbar {
        position: absolute !important;
    }
    
}
