css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bitcoin-24.com") {

body {
color:#aaa!important;
background-color:#0B0B0B!important;
}

.well {
background-color:#252525!important;
}

a {
color:#555!important;
}

.nav-list > li > a, .nav-list .nav-header, .btn, .input-prepend .add-on, .input-append .add-on {
text-shadow:none!important;
}

.nav > li > a:hover {
background-color:#252525!important;
}

.nav-list > .active > a, .nav-list > .active > a:hover {
background-color:#252525!important;
}

.btn-primary {
background-color:#252525!important;
background-image:-moz-linear-gradient(center top , #252525, #0B0B0B)!important;
background-image:-webkit-linear-gradient(top, #252525, #0B0B0B)!important;
}

.btn:hover, .btn-primary:hover, .btn-primary:active, .btn-primary.active, .btn-primary.disabled, .btn-primary[disabled] {
background-color:#0B0B0B!important;
}

#chatbox {
background:none repeat scroll 0 0 #252525!important;
}

.form-search input, .form-inline input, .form-horizontal input, .form-search textarea, .form-inline textarea, .form-horizontal textarea, .form-search select, .form-inline select, .form-horizontal select, .form-search .help-inline, .form-inline .help-inline, .form-horizontal .help-inline, .form-search .uneditable-input, .form-inline .uneditable-input, .form-horizontal .uneditable-input, .form-search .input-prepend, .form-inline .input-prepend, .form-horizontal .input-prepend, .form-search .input-append, .form-inline .input-append, .form-horizontal .input-append {
background-color:#252525!important;
}

.add-on {
background-color:#252525!important;
}

button, .btn {
background-color:#252525!important;
box-shadow:none!important;
}

.btn {
background-image:-moz-linear-gradient(center top , #252525, #0B0B0B)!important;
background-image:-webkit-linear-gradient(top, #252525, #0B0B0B)!important;
}

.btn.active, .btn:active, .btn-group > .btn-primary {
background-image:none!important;
}

input, textarea, select, .uneditable-input, #chatbox, .input-prepend .add-on, .input-append .add-on, .btn, .table-bordered th, .table-bordered td, .table-bordered, .table th, .table td {
border-color:#4D4D4D!important;
}

.table-striped tbody tr:nth-child(2n+1) td, .table-striped tbody tr:nth-child(2n+1) th {
background-color:#252525!important;
}

.table tbody tr:hover td, .table tbody tr:hover th {
background-color:#484848!important;
}

input:focus, textarea:focus {
box-shadow:none!important;
}

input, textarea, select, .uneditable-input, .btn {
color:#aaa!important;
}

table {
background-color:#252525!important;
}

a.brand > img {
display:none;
}

a.brand {
background:url('http://i.imgur.com/VpGBwie.png') no-repeat center left;
width:195px;
height:33px;
left:25px;
position:relative;
}

.btn-success {
background-image:none!important;
background-color:#252525!important;
}

.btn-success:hover, .btn-success:active, .btn-success.active, .btn-success.disabled, .btn-success[disabled] {
background-image:none!important;
background-color:#484848!important;
}

.nav-tabs > .active > a, .nav-tabs > .active > a:hover {
background-color:#252525!important;
}

.tabs-left > .nav-tabs {
border-right: 1px solid #252525!important;
}

.tabs-left > .nav-tabs > li > a:hover,
.tabs-left > .nav-tabs .active > a, .tabs-left > .nav-tabs .active > a:hover {
border-color: #252525!important;
}

.form-actions {
background-color:#252525!important;
border:1px solid #4D4D4D!important;
}

legend {
border-color:#4D4D4D!important;
}

legend, label {
color:inherit!important;
}

hr {
border-color:#4D4D4D!important;
}

#submitmsg {
height:28px!important;
}

}
