css:
@-moz-document domain('facebook.com')  {

#blueBar {
background-color: #FFF !important;
border-color: transparent !important;
}

#pageLogo a {
background-color: #FFF;
background: url("http://i.imgur.com/Up7HK.png") no-repeat center !important;

}

#pageLogo {
margin-left: -6px;
z-index: 5;
}

#navHome a {
color: #d3d9e8 !important;
}

#navHome a:hover {
color: #fff !important;
}

/* part of this next bit hides some of the facebook info in the right bar. 
   am i allowed to do that? is that illegal? sorry :-( */
.fbTimelineSideAds,
#rightCol #pagelet_ego_pane .ego_column:nth-child(1),
.fbPhotoSnowboxAds,
.fbPhotoSnowboxAdsSide,
.fbEmu.fbEmuBlock.fbEmuEgo[id*="-id_"],
a[href*="/campaign/111111"], 
.fbEmuMarketplace,
#egoRefreshAds,
#pagelet_ego_pane_m,
#pagelet_ego_pane_w,
.fbAdUnit,
#pagelet_rhc_footer,
.rhcFooterWrap,
.profile_sidebar_ads, .sidebar_ads, .adcolumn_header, .admarket_ad, .ad_story, .social_ad_advert, .ads_feedback, .next_ad_button, .more_ads, #pagelet_bookmark_nav,
.headerTinymanName
{
background:none !important;
display:none !important
}

#fbDockChat,
#rightCol {
display: block !important;
}
    
}
