css:
@-moz-document url-prefix('http://answers.microsoft.com/ja-jp') {
/*== answers.microsoft.com/ja-jp/ ==*/

/*-- unwanted items --*/

/*-- page fit to window width --*/
body {min-width: inherit !important;}
.contentChannel {width: 100% !important; padding: 0 !important;}

/*-- header --*/
#Masthead {padding: 0 !important;}
#MastheadBigTitle
 {display: inline !important; margin-top: 0 !important; font-size: 1.2em !important;}
#NavigationContainer {margin: auto !important;}
#TopNav > li:first-child {margin-left: auto !important;}
#TopNav > li > a, #TopNav > li > button {line-height: normal !important;}
#HomepageWelcome {margin: auto !important;}
img.heroGraphic {max-width: 100% !important;}

/*-- search and theme --*/
#SearchAndTheme {margin: auto !important;}
#ForumLogo, #SearchBox {margin: auto !important; width: auto !important;}
#SearchBox.positionRight br {display: none !important;}
#SearchForm {margin: auto !important;}

/*-- article thread 2columns relayout --*/
#ThreadContent, .threadPreview
 {width: 100% !important; margin-right: -220px;}
#ThreadContent>div, .threadPreview>div
 {width: auto !important; margin-right: 224px}

/*-- faq (http://answers.microsoft.com/ja-jp/Page/faq) --*/
#faq p, #gettingstarted p {overflow: auto;}

/*-- footer --*/
#Footer {margin: auto !important; padding: 0 !important;}
#FooterLinks {left: 0 !important;}
#LocalePopup .dropNavColumn > li {width: auto !important;}
.footerLinks {margin: auto !important;}
a, .msaActionText, .msaButton {white-space: normal;}


/*=== genle index page (http://answers\.microsoft\.com/ja-jp/\w+) ===*/
#LobForumTitle, .lobCategory
 {width: 100% !important; margin-bottom: auto !important;}
/*-- trend --*/
#trending {width: 100% !important;}

}
