css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youku.com") {

*{
    font-size:12px !important;
}

.YK_head .info .username{
    top: 0px !important;
}

.yk-so,.YK_search,.YK_gotop,.YK_help,.bottomRow,.yk-footer,.sub,.summary,.spacer,YK_help,.footerBox,div.YK_topzone,html body div.window div.screen div#miniheader.s_miniHeader div.headerBox div.nav ul.prinav li a{
    display:none !important;
}

.YK_head .YK_opacity, .YK_subhead .YK_opacity {
    background-color: #F3F3F3 !important;
}

.YK_layout_2 .YK_body {
    background: none repeat scroll 0 0 #F3F3F3 !important;
}

body {
    background: none repeat scroll 0 0 #F4F4F4 !important;
}

.title{
    font-size:14px !important;
}
.s_miniHeader .subnav .handle{
    width:28px !important;
}
.ucenter .notify .handle{
    width:28px !important;
}
.ucenter .watching .handle{
    width:56px !important;
}
.ucenter .upload a{
    width:28px !important;
}
.s_miniHeader .ucenter{
    width:290px !important;
}
}

@-moz-document domain("v.youku.com") {
.s_footer,#sideTool,.crumbs,.left,.right,.sideCol,.mainCol {
   display:none !important;
}
}
