css:
@-moz-document domain("youtube.com") {

html.guide-pinned body#body.ltr div#body-container div#page-container div#page.home div#content.content-alignment div.branded-page-v2-container div.branded-page-v2-col-container div.branded-page-v2-col-container-inner div.branded-page-v2-primary-col div.yt-card div#gh-activityfeed.branded-page-v2-body div#feed.spf-nolink div#feed-main-what_to_watch.individual-feed div.feed-container ul.feed-list li.feed-item-container div.feed-item-dismissable div.feed-item-main div.feed-item-main-content div.shelf-wrapper div.compact-shelf div.compact-shelf-content-container button.yt-uix-shelfslider-next{
display:none !important;
}

#watch7-sidebar,.watch-sidebar-section,div#rc,.branded-page-v2-secondary-col{
display:none !important;
}

.site-left-aligned.flex-width-enabled #alerts, .site-left-aligned.flex-width-enabled #page, body.site-left-aligned.flex-width-enabled.guide-expanded #ticker .ytg-wide, body.site-left-aligned.flex-width-enabled.guide-expanded #alerts, body.site-left-aligned.flex-width-enabled #header{
width:auto !important;
max-width:inherit !important;
min-width:inherit !important;
margin-right:auto;
}

#player.watch-small #ytOlinks{
margin-top:0 !important; 
}

#movie_player > div.html5-video-container > video,#movie_player,#watch7-main{
 width:100% !important; 
}

.feed-item-container,.feed-item-main-content{
max-width:100% !important;
}

html body#body.ltr div#body-container div#page-container div#page.watch div#player.watch-small div#player-mole-container div#player-api.player-width div#movie_player.html5-video-player div.html5-video-container video.video-stream{
left:0px !important;
}

/*.html5-main-video{
width:100% !important;
}*/

#Apage{
width:83% !important;
}

#page,.feed-item-main-content{
width:auto !important;
}

#content{
/*width:70% !important;*/
}

.site-left-aligned{
width:100% !important;
}

.player{
width:100% !important;
}

.player-width{
width:100% !important;
}

#watch7-content{
width:100% !important;
}

.watch-sidebar{
min-width:100% !important;
overflow:visible !important;
}

#watch7-sidebar{
margin-left:0px !important;
}

#ytOlinks,#watch7-playlist-tray-container{
float:left !important;
}

#watch-discussion .comments-iframe-container{
max-width:100% !important;
}
.action-panel-content,#watch-description-clip{
width:auto !important;
}
#comments-test-iframe,#comments-test-iframe iframe{
width:100% !important;
}
#footer{
margin-left:0px !important;
margin-right:0px !important;
}
#footer-main{
margin:auto !important;
width:100% !important;
text-align:center !important;
}
#footer-links{
margin:auto !important;
width:50% !important;
text-align:center !important;
}
}
