css:
@-moz-document regexp('http://(www\\.)?penny-arcade\\.com/report/(article|list)/.*') {
body {
    background: #000 !important;
    color: #fff !important;
}

#main {
    background: #000 !important;
}

#header {
    background: #555 !important;
}

#categories,
#header #social li a {
    background-color: #555 !important;
}

.EmbedPAReport h2,
#gameGuide td,
#nav {
    background: #777 !important;
}

.EmbedPAReport .EmbedCommentForm .TextBox {
    background: #bbb !important;
    border: 1px solid #666 !important;
}

#searchFilter input.inputSearch {
    background: #888 url(http://hw1.pa-cdn.com/par/img/sprite_icons.png) -1px -749px no-repeat !important; 
    border: 1px solid #666 !important;
}

div.Comment,
div.EmbedCommentForm,
.EmbedPAReport .Pager,
#articleNav,
#articleNav .meta .date,
#articleNav .tags p,
#articleNav .content {
    background: #888 !important;
}

.EmbedPAReport .Item .DateCreated,
#article .copy h6,
#article .copy h3,
.related a,
#articleNav .meta span,
#articleNav .meta li {
    color: #fff !important; 
}

#articleNav .btnHideTags {
    background: #666 url(http://hw1.pa-cdn.com/par/img/sprite_icons.png) 12px -843px no-repeat !important;
}

#articleNav .share {
    border-right: 2px solid #bbb !important;
    border-left: 2px solid #bbb !important;
}
#articleNav .btnCmnts {
    border-right: 2px solid #bbb !important;
}

#category li li {
    background: #aaa !important;
}

#articleNav .tags a {
    color: #ddd !important;
}

.headlines li a {
    color: #fff !important; 
    background: #333 !important;
}
.related li {background: #333 !important;} 

#article .img .credit {
    background: #222 url(http://hw1.pa-cdn.com/par/img/sprite_icons.png) 12px -814px no-repeat !important;
}

.headlines li p {
    background: none repeat scroll 0% 0% !important;
}
}
