css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.hushmail.com/preview/hushmail/#") {

html {background-color:#111 !important;}
#element_message-list-table {background-color:#222 !important;}
.read-message, .active, div.group label, .info, #user-info {color:#ccc !important;}
li.element_folder-list-node-child {
    background-color:#555 !important;
    color:#eee !important;
}
li.element_folder-list-node-child * {
    background-color:transparent !important;
    color:#eee !important;
}
li.element_folder-list-node-child:hover, li.element_folder-list-node-child *:hover {
    background-color:#333 !important;
    color:#ccc !important;
}
p {color:#111 !important}
.messages-options { background-color:#444 !important;}
.messages-options a:hover {background-color:#555 !important; color:#ccc !important;}
.bottom {background-color:#555 !important; border-color:#555 !important;}
.rounded {
    background-color:#555 !important;
    color:#eee !important;
    border-color:#333 !important;
}
.rounded:hover{
    background-color:#333 !important;
    color:#ccc !important;
    border-color:#111 !important;
}
#body-section {
    background-color:#333 !important;
    border-color:#111 !important;
}
#full-headers .headers th, .secondary {color:#222 !important;}
.element_test-get-more-features-link {display:none;}
div.box, div.top {border-color:#555 !important; background-color:#555 !important;}
iframe {background-color:#fff !important;}
.pages * {background-color:#333 !important}
.pages a {background-color:#444 !important;}
.pages a:hover {background-color:#222 !important; color:#ccc !important;}
.pages .current {background-color:#2a2a2a !important;}
.logo-inner{background-image:url('http://i.imgur.com/7Xf9Gao.png'); background-repeat:no-repeat;}
.logo-inner img {visibility:hidden;}
}
