css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

  /*
    Resize player
  */
  #player-api {
    width:1065px !important;
    height:490px !important;
  }

  /* HTML5 Player*/
  .html5-video-content, .video-stream.html5-main-video {
    width:100% !important;;
    height:100% !important;
  }
  
  /*
    Dark gray theater background
  */
  #theater-background {
    background-color:#1b1b1b !important;
    height:490px !important;
  }
  #player {
    margin-top:0px !important;
  }

  /*
    Fix 'related videos' sidebar when 'small player' button is activated
    so it doesn't overlap with the video
  */
  #watch7-sidebar {
    margin-top:10px !important;
  }
  #watch7-sidebar-playlist {
    padding-left:10px !important; 
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1); /* same shadow as other boxes */
  }
   
 
  @media screen and (min-width:1281px) and (min-height:782px){
    #player-api {
      width:1280px !important;
      height:725px !important;
    }
    #theater-background {
      background-color:#1b1b1b !important;
      height:725px !important;
    }
    #player {
      margin-top:0px !important;
    }
    #watch7-sidebar {
      margin-top:130px !important;
    }
  }


}
