css:
@-moz-document url-prefix("http://blog.livedoor.jp/dqnplus/") {
.twitter-share-button,
#poweredAdsBy,
#ld-topics,
.menu,
.related-articles,
.related-articles + center,
#tweetlist,
#comment,
#takecomment,
#formbody,
#footer,
.social-buttons,
.side-navi,
.popularArticlesWithImage,
#right,
#articletop,
.pager,
.ad-space,
div#title,
div#left {
  display:none !important;
}

* {
  background-image:none !important;
}

#container,
#blogcontainer,
#wrapper,
#content,
#blog {
  width:100%;
}

#blogcontainer,
#container {
  padding:0;
  margin:0;
}

h3.entry-title {
  font-size:20px !important;
  padding-left:0 !important;
}

h2.date {
    padding-left:0 !important;
}
}
