css:
@-moz-document url-prefix("http://guildwarstemple.com/dragontimer/") {
#dragoncalc,
#dragoncalc2{
  float: initial !important;
  width: auto !important;
  padding-top: 0 !important;
  clear: none !important;
}
.eventTimeBox {
  float: none !important;
  clear: none !important;
  margin: 0 !important;
}
.eventTimeBox,
#timer-image-div,
#timer-ads{
  display: inline-block !important;
  float: none !important;
  
}
#page,
.singular #content,
.left-sidebar.singular #content,
#timer-content {
  width: 1170px !important;
  max-width: 1170px !important;
}
#dragoncalc > h2,
#dragoncalc2 > h2{
  display: none !important;
}
#midfloater {
  float: none !important;
  width: auto !important;
}
#branding img {
  margin-bottom: -100px !important;
  margin-left: 0 !important;
}
#timer-ads {
  clear: both !important;
}
.event-info:hover span,
.history:hover span {
  z-index: 100 !important;
}
#infoupdater {
  height: auto !important;
}
}

@-moz-document url("http://guildwarstemple.com/apps/timers.php") {
.eventTimeBox {
  margin: 0 !important;
}
#eventset1, #eventset2, #eventset3, #eventset4 {
  margin: 0 !important;
}
#eventsets h2 {
  display: none;
}
#timer-content {
  width: 935px !important;
}
#eventset3 {
  position: relative !important;
  top: -115px !important;
}
@media (min-width: 1221px) {
  #timer-content {
    width: 1221px !important;
  }
}
@media (max-width: 1220px) {
  #eventset3 {
    position: initial !important;
    top: initial !important;
  }
}
.gs_bracket2 {
  top: 115px !important;
}
.gs_bracket3 {
  top: 230px !important;
}
.gs_bracket4 {
  top: 345px !important;
}
#info-n-guesting {
  width: 649px !important;
}
}
