css:
@-moz-document domain("eztv.it") {
a.epinfo {
  opacity:1;
}


a.epinfo[title*='720p'] {
  opacity:0.5;
}


a.epinfo[title*=MVGroup] {
  color: #4f95dd;
}


a.epinfo[title*=MVGroup][title*=of] {
  font-weight:normal;
}


a.epinfo:visited {
  color: #D3D5E5 !important;
}


a.magnet {
  clear: both
}


#header_logo,
.section_thread_post > a,
.section_thread_post > b,
.section_thread_post > span,
.section_thread_post > br {
    display: none
}
}
