css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

body{
background:url(http://s20.postimg.org/89si9aij1/blah.jpg)!important
}
A.related-video.yt-uix-contextlink.yt-uix-sessionlink{
  background: #E0BEBC!important;
  }
#yt-masthead-container {
  background: #E0BEBC!important;
  border-bottom: 0px !important;
  padding: 17px 0 10px!important;
}
 #logo {
  background: url(http://s20.postimg.org/e1k7n4xxp/logo.png)!important;
  height: 62px!important;
  width: 315px!important;
  margin-top:-10px!important;
  margin-left:-12px!important;
  border-radius:7.5px!important
  
}

.content-region{display:none!important}

a, .view-count, h3 {
  color: #AB3A70!important;

}
.lohp-blog-headline, .lohp-video-metadata {
  color: #E0BEBC!important;

} 
.yt-uix-button-primary, body .yt-uix-button-primary[disabled] {
  background: #AB3A70!important;
  border-color: #AB3A70!important;
}
#guide-container .guide-item.guide-item-selected, #guide-container .guide-item.guide-item-selected:hover {
  background: #E0BEBC!important;
  border-color: #AB3A70!important;
}
.guide-section-separator {
  border: none!important
}
.signup-promo.guided-help-box:after, .signup-promo.guided-help-box {
display:none!important;

}
ul.guide-toplevel{
background: rgba(224, 190, 188, 1)!important
}
#guide-subscriptions-container img{
height:0px!important;
padding:18px!important;
background:url(http://s20.postimg.org/w1y059fih/www.png)!important

}
body #masthead-expanded #howto-addto, .yt-tile-default, .yt-tile-default a, .yt-tile-visible, .yt-tile-visible a{ 
background: #AB3A70!important
}

.yt-uix-button-subscribe-unbranded  {
  background:transparent!important;
  border:none!important

}
.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal{
background: #AB3A70!important;
border: #AB3A70!important
}
.feed-author-bubble .system-icon.rec {
  background: url(http://s20.postimg.org/4h56dzxzh/www_guide_vflyu_Dyxj.png) no-repeat scroll 0 -1330px transparent!important;
  
}
.feed-promo-image {
  height: 0px!important;
  padding-top: 104px!important;
  background:url(http://s20.postimg.org/pcrgp8u6h/promo_home_icon_vfl_Gv_Cg_AU.png)!important
}
#watch-related.video-list{
background: rgba(224, 190, 188, 1)!important
}
 #player {
background:transparent!important;

}

#guide-container.collapsible-guide.branded,#watch7-sidebar{
background: transparent!important
}
#watch7-playlist-bar-controls, .watch7-playlist-bar-left, .watch7-playlist-bar-notifications, .watch7-playlist-bar-secondary-controls {
  background: #AB3A70!important;
  border:none!important

}
.video-list-item a,#watch7-playlist-tray, #watch7-playlist-tray-mask {
background: #AB3A70!important;
border:#AB3A70!important

}
#c4-header-bg-container {
  background-image: url(http://s20.postimg.org/tcym1hknh/one_direction_intro.jpg)!important;
}

.masthead-search-terms-border {
  border: 1px solid #E0BEBC!important;
}
}
