@-moz-document domain("gawker.com") {

.site-logo{
background: url(http://s20.postimg.org/qm9e2ax59/GWKRLOGO1.png)no-repeat center center!important;
}

.replyform .editor-buttons,.replybranch-ng .reply.unapproved,.button.dropdown .show-dropdown, .top-bar .dropdown, form .custom.dropdown,.reply.unapproved{
