css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fourmizzz.fr") {
    #menu ul.menu_colonne {
        width: 19.5% !important;
    }
    #menu ul.menu_colonne li {
        width: 100% !important;
        height: 30px !important;
        margin:0 !important;
        text-align: left !important;
        line-height: 30px !important;
    }
    #menu ul.menu_colonne li a div {
        float:left !important;
        margin: 0 5px !important;
    }
    @media screen and (min-width: 1000px) {
        #menuAide, #menuDeconnexion {left: 76.5% !important;}
        #menuComptePlus {left: 57.5% !important;}
        #menuCommunaute {left: 38.4% !important;}
        #menuAlliance {left: 19% !important;}
    }
    @media screen and (max-width: 999px) {
        #menuAide, #menuDeconnexion {left: 77.5% !important;}
        #menuComptePlus {left: 62% !important;}
        #menuCommunaute {left: 46% !important;}
        #menuAlliance {left: 30% !important;}
        #menuFourmiliere {left: 14% !important;}
    }
}
