css:
@-moz-document 

/* flickr.com/photo/shot , flickr.com/groups/iran */

url-prefix(http://www.flickr.com),
url-prefix(http://flickr.com) {

body, input, textarea, select { font-family: Tahoma !important; }
#DiscussPhoto { 
   text-align: right;
   direction: rtl;
}
.photoDescription {
   text-align: right;
   direction: rtl;
}
p {
   text-align: right;
   font-family: tahoma;
   font-size: 9pt !important;
}
.poollist {
   font-size: 7pt !important;
}
a {
   font-size: 8pt !important;
   text-decoration: none !important;
   border-bottom: 0px;
}
.links {
   text-align: left;
}
.do {
   text-align: left;
   font-size: 6pt !important;
}
.Privacy {
   text-align: left;
   font-size: 8pt !important;
}
.Activity {
   text-align: left;
}
.desc {
   font-size: 8pt !important;
   text-align: right;
   direction: rtl;
}
.photo {
   text-align: center;
}
h4 {
   text-align: right;
   direction: rtl;
}
#photoswftd {
   text-align: right;
   direction: rtl;
}
.Said {
   text-align: right;
   direction: rtl;
   Background: #EFEFEF;
   border: 1px solid #DDD;
   color: #333 !important;
}
.said h4 {
   text-align: left;
   direction: ltr;
}
.said a {
   color: #A0B241 !important;
}
.said a:hover {
   background: #A0B241 !important;
   color: #FFF !important;
}
}
