css:
@-moz-document domain("lifehacking.jp") {

#header, #big-sidebar, .breadcrumbs, #site-footer, .scrollup
{ display: none !important; }

.wide,
#primary.small-disable .content-col.col-main {
  width: auto !important;
}

.wpi-box {
  width: 640px;
  margin: 2em auto;
  padding: 40px;
  box-shadow: 0 0 4px #ddd;
  border-radius: 2px;
}

.content-col {
  margin: 0;
}

body {
  background: #eee;
}

}
