css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://craftsite.pl/forum") {
#header_bar.clearfix{
    background: none!important;
    padding: 10px;
    text-align: right;
    position: fixed!important;
    width: 200px!important;
    z-index: 99999!important;
    float:right!important;
    width:100%!important;
}
ul.ipsList_inline.right {
    background-color: rgba(50, 50, 50, 0.2)!important;
    border-radius: 4px!important;
    padding-right: 12px!important;
    float:right!important;
    margin-right:0%!important;
    transition: background 0.2s, box-shadow 0.2s;
    -webkit-transition: background 0.2s, box-shadow 0.2s;
}
ul.ipsList_inline.right:hover {
    background-color: rgba(50, 50, 50, 0.8)!important;
    border-radius: 4px!important;
    padding-right: 12px!important;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 1)!important;
}
#branding {
    padding-top: 40px!important;
}
#admin_bar{
    background-color: rgba(50, 50, 50, 0.2)!important;
    border-radius: 4px!important;
    padding-left:7px!important;
    padding-right:7px!important;
    transition: background 0.2s, box-shadow 0.2s;
    -webkit-transition: background 0.2s, box-shadow 0.2s;
}
#admin_bar:hover{
    background-color: rgba(50, 50, 50, 0.8)!important;
    border-radius: 4px!important;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 1)!important;
}
#inbox_link, #notify_link{
border-radius:4px!important;
}
}
