css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

  /* remove names and icons */

  td.thumb,
  td.content strong,
  img[alt="Icon_red_lock"]{
    display:none;
  }

  /* remove tools */

  span.entry-meta{
    visibility:hidden;
  }

  span.entry-meta a.entry-date{
    visibility:visible;
  }

  /* others */

  td.content{
    padding-left:10px !important;
  }

}
