css:
@-moz-document 
domain("youtube.com")

{
/* Like & Dislike Bar Overflow */
.video-extras-sparkbars {
height: 4px !important;
margin: 1px 0px 5px 0px !important;
overflow: visible !important;
border-style: double !important;
border-width: 1px !important;
}

/* Like & Dislike Bar Colors & Borders */
.video-extras-sparkbar-likes {
 background-color: #00FF00 !important;
 border-color: #00FF00 !important;
 box-shadow: 0px 0px 4px 1px rgba(0, 255, 0, 1) !important;
}
.video-extras-sparkbar-dislikes {
 background-color: #FF0000 !important;
 border-color: #FF0000 !important;
 box-shadow: 0px 0px 4px 1px rgba(255, 0, 0, 1) !important;
}

}
