css:
@-moz-document domain("torrentz.eu") {

    /* RESULT PAGE */
    body {
      margin:0 0.2em;
      width:auto !important;
      min-width:400px;
      max-width:1200px;
    }
    form.search {
      clear:right;
    }
    form.search > fieldset {
      height:auto !important;
    }
    form.search > fieldset > div:first-child {
      float: left !important;
      margin-right: 0 !important;
      width: 80% !important;
    }
    form.search #thesearchbox {
      font-size: 1.5em !important;
      padding: 0 !important;
      width: 100% !important;
    }
    form.search > fieldset > div {
      float: right !important;
      height: auto !important;
      padding: 4px !important;
      width: 15% !important;
    }
    form.search #thesearchbutton {
      font-size: 1.5em !important;
      height: auto !important;
      width: 100% !important;
    }
    div.results > dl > dt {
      width: auto !important;
      margin-right:260px !important;
    }
    
    /* DOWNLOAD PAGE */
    div.download > h2 {
      clear:right !important;
      width:auto !important;
    }
    div.download > dl > dt > a {
      width:auto !important;
    }
    div.download > dl > dt > a > span.u {
      width:auto !important;
    }
    div.download > dl > dt > a > span.n {
      width:auto !important;
    }
    div.recent {
      width:auto !important;
    }
    a:hover {
      color:#369 !important;
    }
    a:visited, a:visited:hover {
      color:#75808A !important;
    }

}
