@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deezer.com") {
@font-face {
  font-family: 'Boogaloo';
  font-style: normal;
  font-weight: 400;
  src: local('Boogaloo'), local('Boogaloo-Regular'), url(https://themes.googleusercontent.com/static/fonts/boogaloo/v3/1x_XysQB9ZwoSzu9a0nb7PesZW2xOQ-xsNqO47m55DA.woff) format('woff');
}

/***************** Header ********************/
    #head{font-family: Boogaloo !important;}

    .logo-deezer{background: none !important;}
    .logo-deezer:after{
        content: 'Deezer'!important;
        display: inline-block !important;
        font-style: italic !important;
        color: #E9E4CA !important;
        text-shadow: 1px 1px #C94C46 !important;
        font-size: 37px !important;
        margin-top: -10px !important;
        font-family: Boogaloo !important;
    }
    
    .page-header .navbar .nav-user .nav-profil li.restriction,.page-header .navbar .nav > li.dropdown .dropdown-menu,.page-header .navbar .nav > li.active > a, .page-header .navbar .nav > li:hover > a{background: #C94C46 !important;}
    .page-header .navbar .nav-user .nav-profil li > a .username{font-family: Boogaloo !important;font-size: 16px !important;}
    
    .header_profil-user .user-avatar img{border-radius: 34px !important;}
    #img_avatar_mini{border-radius: 15px !important;}
    #top-bar,
    .player,
    #current-playlist,
    .navbar,
    .page-header .topbar{background: #28616A !important;}
    #options li, #options li a{border-radius: 29px !important;}
    .page-header .player .cell-player .wrapper{border-radius: 0px !important;background: #C94C46 !important;}
    .page-header .player .cell-player .wrapper .track-progress .progress-bar.preview, .page-header .player .cell-player .wrapper .track-progress .progress-bar.current{background: #28616A !important;}
        
/***************** Content ********************/
    #naboo_menu a{color: #fff !important;}
    #naboo_menu_list_music .tinyscroll_overview a:hover,
    #naboo_menu_list_music .tinyscroll_overview > a:hover .blk_icn_right, 
    #naboo_menu_list_music .tinyscroll_overview .block_folder a:hover .blk_icn_right, 
    #naboo_menu_list_music .tinyscroll_overview .apps_folder a:hover .blk_icn_right{color: #000 !important;background: #28616A !important;}
    #naboo_menu_list_music{background: #C94C46 !important;}
    

    .thumbnail.thumbnail-bordered{padding:10px !important;background:#fff !important;box-shadow: 2px 2px 4px #000 !important;}
    #naboo_artist_name,
    .top-titles h3{font-family: Boogaloo !important;}
    .naboo_discography_album_type{background: transparent !important;font-family: Boogaloo !important;font-size:24px !important;}

}
