css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {
	#mainBar {
		margin-right: 4px !important;
	}
	#sideArea { 
		display: none !important; 
	}
	#floatingBar {
		box-shadow: 0 1px 10px rgba(20, 20, 20, 0.4);
		width: auto !important;
		right: 48px;
		padding: 4px !important;
	}
	#floatingTitleBar {
		display: none !important;
	}
	.entryBody {
		max-width: 100% !important;
	}
	.entryBody img, .entryBody img[style], .entryBody table, .entryBody table td {
		display: inline-block !important;
		overflow: visible;
		float: none !important;
		width: auto !important;
		height: auto !important;
		max-height: none !important;
		max-width: none !important;
	}

	#feedlyTabsHolder, #feedlyTabs {
		height: 100% !important;
		margin-top: 0 !important;
		margin-bottom: 0 !important;
		padding-top: 0 !important;
		padding-bottom: 0 !important;
	}
	#feedlyFrame, #feedlyPart, #feedlyPage, #mainArea {
		margin-left: 0 !important;
		margin-right: 0 !important;
		padding-left: 0 !important;
		padding-right: 0 !important;
		width: 100% !important;
	}
}
