css:
@-moz-document url-prefix("http://www.feedly.com/home") {
    .area{
        border-color:rgba(45,45,45,1) !important;
        background-color:rgba(45,45,45,1) !important;
    }
    
    h1#feedlyTitleBar {color: rgba(255,255,255,1) !important;}
    
    #floatingBar {background-color: rgba(45,45,45,.7) !important;}

    #floatingBar h1 {color: rgba(255, 255, 255,1) !important;}
    
    .highlightableIcon:hover {
        border:1px solid rgba(255,255,255,1) !important;
    }
    .highlightableIcon {
        opacity:1 !important;
        background-color: rgba(255,255,255,1) !important;
    }
    
    .entryHolder {background-color: rgba(45,45,45,1) !important;}

    .pageActionBar {
        background-color: rgba(130,189,20,.85) !important;
        border-top-left-radius: 3px !important;
        border-bottom-left-radius: 3px !important;
        box-shadow:0 0 7px rgba(130,189,20,.5) !important;
    }
    
    .u5Entry {
        border: 1px solid #333 !important;
        background-color: #333 !important;
    }
    
    .u5Entry .title.unread {color: rgba(255,255,255,1) !important;}
    
    .column .inlineFrame {
        box-shadow: 0px 0px 999px rgba(0, 0, 0, 1) !important;
        border: 1px solid rgb(45, 45, 45) !important;
    }
    
    .inlineFrame {
        background-color: rgba(45,45,45,1) !important;
        border:1px solid rgba(234, 234, 233,.3) !important;
    }
    
    .u100Entry {background-color: rgba(45,45,45,1) !important;}
    
    .inlineFrame .title.read {color: rgb(255,255,255) !important;}

    .u0Entry {border:1px solid rgba(255,255,255,.03) !important;}
    .unread {color: rgb(255, 255, 255) !important;}
    .u0Entry:hover .unread {color: rgb(17, 17, 17) !important;}
    .u0Entry .quicklistHandle{background-color:rgba(255,255,255,.5) !important;}
    .u0Entry:hover .quicklistHandle{background-color:rgba(255,255,255,0) !important;}

    #sideArea .feedIndexTitle {color: rgba(255,255,255,.8) !important;}

    .u100frame.selectedEntry {background-color: rgba(45,45,45,.1) !important;}
    
    .tagged .tagLabel {color: rgba(255,255,255,1) !important;}
    .viewerIcon.hi {
        border: 0px solid rgb(130, 189, 26) !important;
        background-color: rgb(130, 189, 26) !important;
        opacity: 1 !important;
    }
    
    .entryBody {color: #afafaf !important;}

    .entryBody h,.entryBody h1,.entryBody h2,.entryBody h3,.entryBody h4,.entryBody h5 {
        color:#dfdfdf !important;
    }

    .entryBody strong{color: #dfdfdf !important;}

    .inlineFrame .entryBody img {
        max-width: 600px !important;
        margin-left: auto !important;
        margin-right: auto !important;
    }
    
    .entryBody iframe {
        max-width: 600px !important;
        margin-left: auto !important;
        margin-right: auto !important;
    }
}
