body > #body-container > #yt-masthead-container > #yt-masthead {
	width: 1130px !important;
    margin: 0 auto !important;
}

body > #body-container > #page-container > #page {
	width: 1130px !important;
    margin: 0 auto !important;
    padding-left: 0 !important;
}


body > #footer-hh-container > #footer-hh {
	width: 1130px !important;
	margin: 0 auto !important;
}


body > #body-container > #page-container > #page > #player, 
body > #body-container > #page-container > #page > #content > #watch7-container > #watch7-main-container {
	padding-left: 180px !important;
}

