css:
body {
    background: white;
}

.tabs-container {
    z-index: 2;
}

.tabs-container .tab {
    border: none;
}

.navigation-container {
    z-index: 3;
}

.right-tools-panel {
    z-index: 2;
}

.wave-container {
    padding: 0;
    z-index: 1;
}

.wave-header {
    height: 50px;
}

.wave-panel {
    width: 100%;
}

.wave-content {
    background-color: #fff;
}

.wave-blips .container-blip:first-of-type {
    padding: 0;
    padding-top: 2rem;
    background-color: #fff;
    border: none;
}

.root-blip ~ .blip-container {
    left: 45%;
    width: 50%;
}

.root-blip {
    z-index: 2;
    position: fixed;
    overflow: auto;
    width: 40%;
    height: 90%;
    margin-top: -2rem;
    padding: 0;
    border: none;
    background-color: #f5f5ff !important;
    border-right: .1rem solid #e5e5ff;
}

.root-blip.active {
    background-color: #f5f5ff !important;
}

.root-blip > .blip-info {
    left: 0;
    top: 2.2rem;
    color: orange;
}

.root-blip > .blip-info .avatar {
    display: none;
}

.root-blip > .editor-container {
    padding: 2.5rem 2rem 1rem;
}

.active-blip-controls.unvisible {
    display: none;
}

.root-blip> .bottom-blip-menu {
    position: relative;
    left: 2rem;
    bottom: 1rem;
    width: 0;
}

.last-root-blip-menu-cap {
    background-color: white;
    clear: both;
    margin: 2rem 0;
    padding-bottom: 2rem;
}
