css:
@-moz-document domain("www.feedly.com")
{
BODY.home { background: #FFFFFF !important; }

div#feedlyFrame {width : 98% !important;}
div#feedlyPart {width : 98% !important;}
div#feedlyPage, div#mainArea { width: 100% !important;}
div#sideArea {display: none !important;}
.entryHolder { width:100% !important; max-width: 100% !important;}
.content img { margin-left:0px !important;}
.u100Entry { width: 100% !important; }
.u100Entry.selectedEntry {width: none !important; width: 100% !important;}

#recommentationAreaHeadlines>div { width:28% !important }
#recommentationAreaHeadlines .topRecommendedEntry { width: 100% !important; }

.u100frame{ padding:0 0 0 0 !important; }
.u100frame table{ padding:10px !important; }
.u100Entry iframe { margin-left: 0 !important; }
.u100Entry .title { margin-bottom:5px !important }
.area { margin-left:0 !important } 

.entryList .u4Entry,
#mainArea .u10Entry {max-width:none !important;}
#floatingBar{ width: 73% !important;}
#feedlyTabsHolder{margin-top:10px !important; height:100% !important;}
#feedlyTabs {height:100% !important;}

div#mainArea { width:100% !important; }
div#aboutArea {display:none !important;}
A {color:navy !important;text-decoration:underline !important;}
A.title, A.feedTitle {color:black !important;text-decoration:none !important;}
A.sourceTitle {color:gray !important; text-decoration:none !important;}
span.sourceTitle A {color:gray !important; text-decoration:none !important;}
}
